import React, { useState } from 'react';

import { Link, useHistory, Route, Switch } from 'react-router-dom';

import Login from './Login';
import Signup from './Signup';

function UnauthenticatedApp({ setCurrentUser }) {

    let history = useHistory();

    //setCurrentUser passed down from app in this to handleSubmit if resp is .ok so they can access authenticated app after that point

    //do we need to history push to authenticated app if successful submissions?

    const [newUser, setNewUser] = useState(
        {name: '', email: '', username: '', password: '', password_confirmation: ''}
        );

    const [user, setUser] = useState({username: '', password: ''});

    function handleSubmit(e) {

        e.preventDefault();

        if(e.target.id === 'newAccountForm')
        {fetch(`http://localhost:3000/users`, {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(newUser)})
        .then(res => console.log(res))
        }
        else 
        {fetch('/api/login', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(user)})
        .then(res => console.log(res))
        }
    };

    //include a signup button that takes user to '/signup'
    //include a login button that takes user to '/login'

    return (
        <div>

            <Link to='/signup' onClick={() => history.push('/signup')}>
                Signup
            </Link> 

            <Link to='/login' onClick={() => history.push('/login')}>
                Login 
            </Link> 

            <Switch>

                <Route path='/signup'>
                    <Signup newUser={newUser}
                            setNewUser={setNewUser}
                            handleSubmit={handleSubmit} />
                </Route>

                <Route path='/login'>
                    <Login user={user}
                           setUser={setUser}
                           handleSubmit={handleSubmit} />
                </Route>

            </Switch>

        </div>
    );
}

export default UnauthenticatedApp;
